resources.string:
layout(location = POSITION_LOCATION) in vec3 position;
void main() {
    gl_Position = vec4(position, 1);
}

precision mediump float;
layout(location = COLOR_BINDING) uniform vec4 color;
out vec4 outColor;
void main() {
    outColor = color;
}

layout(location = POSITION_LOCATION) in vec3 position;
layout(location = UV_LOCATION) in vec2 vertUV;
layout(location = 0) out vec2 outUV;
void main() {
    gl_Position = vec4(position, 1);
    outUV = vertUV;
}

precision mediump float;
layout(location = 0) in vec2 uv;
layout(location = COLOR_BINDING) uniform vec4 color;
layout(binding = TEXTURE_BINDING) uniform sampler2D mainTexture;
out vec4 outColor;
void main() {
    outColor = texture(mainTexture, uv);
}
